package fisglobal;

public class LoopExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int counter=0;
		while(counter <=5) {
			System.out.println("Inside the while loop");
			counter++;
		}
		
		int flag=0;
		do {
			System.out.println("Inside the do while");
			flag++;
		}while(flag<=5);

	}

}
